bool ModificaCampos(int nControl) {
  // 1 Restaurante, 2 Menu y 3 Usuario
  switch (nControl) {
    case 1:
      return ValidaRest.Nombre ||
          ValidaRest.Descripcion ||
          ValidaRest.TipoCategoria ||
          ValidaRest.TipoRestaurant ||
          ValidaRest.Telefono ||
          ValidaRest.Domicilio ||
          ValidaRest.Sector ||
          ValidaRest.DiasLaborales ||
          ValidaRest.TipoServ ||
          ValidaRest.horario1 ||
          ValidaRest.horario2 ||
          ValidaRest.AM ||
          ValidaRest.PM ||
          ValidaRest.logo ||
          ValidaRest.Ubica;
      break;
    case 2:
      return ValidaMenu.Nombre ||
          ValidaMenu.Descripcion ||
          ValidaMenu.TipoCategoria ||
          ValidaMenu.Precio ||
          ValidaMenu.Extras ||
          ValidaMenu.foto;
      break;
    case 3:
      return ValidaUser.Nombre ||
          ValidaUser.Apellido ||
          ValidaUser.FechaNac ||
          ValidaUser.cp ||
          ValidaUser.NumCel ||
          ValidaUser.foto ||
          ValidaUser.Calle ||
          ValidaUser.Ciudad ||
          ValidaUser.Colonia ||
          ValidaUser.numExt ||
          ValidaUser.numInt ||
          ValidaUser.TipoPreferencia;
      break;
  }
}

